<?phpclass C_Auth extends C_Base{	//	// Конструктор.	//	public function __construct(){		parent::__construct();	}		public function before(){		parent::before();	}		public function action_login(){		$this->title .= ' Авторизация';		$mUsers = M_Users::Instance();		$mUsers->Logout();		$this->head = '<script src="js/auth.js"></script>' ;                    $this->title .=isset($_POST['mail']);                				$this->content = $this->Template('v/v_auth.php');		}                public function action_register(){		$this->title .= 'Регистрация';		$mUsers = M_Users::Instance();		$mUsers->Logout();		$this->head = '<script src="js/registration.js"></script>' ;                                				$this->content = $this->Template('v/v_register.php');		}                 public function action_logout(){		$mUsers = M_Users::Instance();		$mUsers->Logout();		$this->redirect("/");		}}